import styled from "styled-components";

export const Container = styled.table`
    padding: 1rem;
    max-width: 1120px;
    display: flex;
    justify-content: center;

    .body{
        background: var(--shape);
        padding: 0.5rem;
        border-radius: 0.25rem;
    }

    div tr {
        display: inline-flex;
        justify-content: space-around;
    }


`
