import styled from "styled-components";

export const Container = styled.table`
    margin-top: 4rem;
    max-width: 1120px;
    display: flex;
    justify-content: center;


    table {
        width: 100%;
    }

    table .title{
        text-align: left;
        background: none;
    }

    table tr {
        background: var(--shape);
    }

    table .linha {
        margin: 1rem;
    }

    table tr td {
        padding: 1rem;
        border-radius: 5px;
    }
`
