var $IteratorPrototype = require('es-iterator-helpers/Iterator.prototype/implementation');

module.exports = function shimIteratorPrototypeEvery() {
	var polyfill = getPolyfill();

	define(
		$IteratorPrototype,
		{ every: polyfill },
		{ every: function () { return $IteratorPrototype.every !== polyfill; } }
	);

	return polyfill;
};
