import styled from "styled-components";

export const Container = styled.table`
    margin-top: 4rem;
    max-width: 1120px;
    display: flex;
    justify-content: center;


    table {
        width: 100%;
    }

    table thead tr th {
        text-align: left;
    }

    table tbody tr {
        margin: 5px;
    }

    tbody {
        background: var(--shape);
    }

    tbody td {
        padding: 1rem;
        border: none;
        border-radius: 5px;
    }
`
